mapper.select:
SELECT
FROM c_notice a
WHERE a.del_flag = 0 AND a.id = #{id}

mapper.insert:
INSERT INTO c_notice_user (
  id,
  notice_id,
  user_id
) VALUE (
  #{id},
  #{noticeId},
  #{userId}
)

mapper.update:
UPDATE c_notice SET
  del_flag = 1
WHERE id = #{id}

UPDATE c_notice_user SET
  del_flag = 1
WHERE notice_id = #{id}
