mapper.select:
SELECT

FROM c_baby a

WHERE a.office_id = #{officeId}
AND a.del_flag = 0



            AND a.class_id = #{classId}



        AND FIND_IN_SET(a.user_id,(
        SELECT CONCAT(s.str,',',x.str) as "str" from (
        SELECT
        GROUP_CONCAT(DISTINCT a.user_id ORDER BY a.user_id) AS "str"
        FROM c_baby a
        left join sys_user u on u.id = a.user_id
        where a.office_id = #{officeId}
        and a.class_id = #{classId} ) s
        LEFT JOIN (
        SELECT GROUP_CONCAT(DISTINCT id ORDER BY id) AS "str" from sys_user where office_id = #{officeId}
        AND FIND_IN_SET(user_type,'3,4')
        ) x on 1 = 1
        ))


ORDER BY a.create_date DESC
LIMIT #{offset},#{limit}

mapper.update:
UPDATE c_baby SET
  del_flag = 1
WHERE id = #{id}
