mapper.select:
SELECT

FROM c_class a

WHERE a.id = #{id}

SELECT

FROM c_class a

WHERE a.office_id = #{officeId}

    AND a.name LIKE
    '%'||#{name}||'%'
    '%'+#{name}+'%'
    concat('%',#{name},'%')


    AND a.period = #{period}


    AND a.year = #{year}


    AND a.teacher_id = #{teacherId}

ORDER BY a.period ,a.year

SELECT

FROM c_class a

WHERE a.teacher_id = #{teacherId}
